package main

import (
	"encoding/json"
	"fmt"

	"github.com/libsv/go-bt/v2"
)

func main() {
	tx, err := bt.NewTxFromString("")
	if err != nil {
		panic(err)
	}

	tx2, err := bt.NewTxFromString("")
	if err != nil {
		panic(err)
	}

	txs := bt.Txs{tx, tx2}

	bb, err := json.MarshalIndent(txs.NodeJSON(), "", "  ")
	if err != nil {
		panic(err)
	}
	fmt.Println(string(bb))
}
